import { useState } from 'react';
import './index.css';
import InputSelectionButton from './InputSelectionButton';
import SelectedInput from './SelectedInput';
import Calendar from './Calendar';

export default function InputSection() {
  const [pressedBtn, setPressedBtn] = useState(1)
  const [selectedDate, setSelectedDate] = useState('2023-01-01') // TO CHANGE ON TODAY
  const [weigth, setWeigth] = useState(0)
  const [bench, setBench] = useState()
  const [squat, setSquat] = useState()
  const [deadlift, setDeadlift] = useState()
  const [pullups, setPullups] = useState()

  function handleInput(i)
  {
    if(i===1) {return setWeigth}
    if(i===2) {return setBench}
    if(i===3) {return setSquat}
    if(i===4) {return setDeadlift}
    return setPullups
  }

  return (
    <div className="input-section">
      <h1>INPUT SECTION {pressedBtn}</h1>
      <InputSelectionButton onBtnClick={()=>setPressedBtn(1)}/>
      <InputSelectionButton onBtnClick={()=>setPressedBtn(2)}/>
      <InputSelectionButton onBtnClick={()=>setPressedBtn(3)}/>
      <InputSelectionButton onBtnClick={()=>setPressedBtn(4)}/>
      <InputSelectionButton onBtnClick={()=>setPressedBtn(5)}/>
      <SelectedInput value={pressedBtn} onInputChange={()=>setWeigth}/>
      {weigth}
      <Calendar value={selectedDate} onDateChange={setSelectedDate}/>
      {selectedDate}
    </div>
  );
}
